import './App.css';
import { useState } from 'react';

export default function  App() {
  const [first,setFirst] = useState("");
  const [second,setSecond] = useState("");
  const [result,setResult] = useState(null);

  function getRelation(ch) {
    switch (ch) {
      case 'F': return "friends";
      case 'L': return "lovers";
      case 'A': return "affectionate";
      case 'M': return "married";
      case 'E': return "enemies";
      default: return "undefined";
    }
  }

  function flamesGame(a,b){
    const relation = "FLAME";
    const n = relation.length;
    let length = 0;
    const arr1 = new Array(26).fill(0);
    const arr2 = new Array(26).fill(0);

    for(let ch of a){
      arr1[ch.charCodeAt(0)-'a'.charCodeAt(0)]++;
    }
    for(let ch of b){
      arr2[ch.charCodeAt(0)-'a'.charCodeAt(0)]++;
    }
    for(let i=0; i<26;i++){
      length+=Math.abs(arr1[i]-arr2[i]);
    }
    length %= n;

    if (length === 0) {
      length = n;
    }
    const check = relation.charAt(length-1);

    setResult(`${a} and ${b} are ${getRelation(check)}`)

  }

  const handleSubmit=(e)=>{
    e.preventDefault();
    flamesGame(first,second);
  }

  return (
    <div>
      <h1>
        Your Relation with your partner in my hands!
      </h1>
      <form onSubmit = {handleSubmit}>
        <div>
          <label>Input1:</label>
          <input
          type = "text"
          value = {first}
          onChange = {(e)=>setFirst(e.target.value)}
          >
          </input>
        </div>
        <div>
          <label>Input 2:</label>
          <input
          type = "text"
          value = {second}
          onChange = {(e)=>setSecond(e.target.value)}
          >
          </input>
        </div>
        <button type = "submit">Submit</button>
      </form>
      {result &&(
        <div>
          <p>{result}</p>
        </div>
      )}
    </div>
  );

}
